-- Add gallery_urls column and populate for vehicle detail page gallery.
-- Run this after vehicles-table.sql (or if vehicles table already exists without this column).

alter table public.vehicles
  add column if not exists gallery_urls jsonb not null default '[]';

update public.vehicles set gallery_urls = '["https://picsum.photos/seed/vehicle-v1/800/600","https://picsum.photos/seed/vehicle-v1-1/1200/800","https://picsum.photos/seed/vehicle-v1-2/1200/800","https://picsum.photos/seed/vehicle-v1-3/1200/800"]'::jsonb where id = 'v1';
